rules.value:
({ name, tags, frontmatter }) => {

    if (tags.includes("done")) return { after:{text:"☆".repeat(Number(frontmatter.star))}}
    if (!frontmatter.deadline) return {}

    return (new Date(frontmatter.deadline) < new Date(Date.now())) ? { after: { text: "over" }, classname: "overdue" } : { after: { text: "valid" }, classname: "valid" }
}
